import React from 'react';
import './App.css';
import DisplayEmployee from './components/DisplayEmployee';
import FormEmployee from './components/FormEmployee';
import axios from 'axios';

const url = "https://post-a-form.herokuapp.com/api/employees/";
    const config = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    };

      fetch(url,config)
        .then(res => res.json)
        .then(res => {
          if (res.error) {
            alert(res.error);
          } else {
            alert(`Employé ajouté avec l'ID ${res}!`);
          }
        }).catch(e => {
          console.error(e);
          alert('Erreur lors de l ajout d un employé');
        });

const sampleEmployee = {
  gender: 'male',
  name: {
    first: 'Charlie',
    last: 'Thompson',
  },
  location: {
    street: {
      number: 761,
      name: 'Tay Street',
    },
    city: 'Timaru',
    postcode: 76111,
  },
  email: 'charlie.thompson@example.com',
  picture: {
    medium: 'https://randomuser.me/api/portraits/med/men/40.jpg',
  },
};

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      employee: null
    };
    this.getEmployee = this.getEmployee.bind(this);
  }

  componentDidMount() {
    this.getEmployee();
  }

  getEmployee() {
    // Send the request
    axios.get('https://randomuser.me/api?nat=fr')
      // Extract the DATA from the received response
      .then(response => response.data)
      // Use this data to update the state
      .then(data => {
        this.setState({
          employee: data.results[0],
        });
    });
  }

  render() {
    return (
      <div className="App">
        {
          this.state.employee ? <DisplayEmployee employee={this.state.employee} /> : <p>No data yet</p>
        }
        <button type="button" onClick={this.getEmployee}>Get employee</button>
        <FormEmployee />
      </div> 
    );
  };
}

export default App;
